
import { CreateReservation } from "@/app/actions";
import prisma from "@/app/lib/db";
import { useCountries } from "@/app/lib/getCountries";
import CategoryShowCase from "@/components/CategoryShowCase/CategoryShowCase";
import { SelectCalender } from "@/components/SelectCalender/SelectCalender";
import { ReservationSubmitButton } from "@/components/SubmitButton";
import { Button } from "@/components/ui/button"
import { Separator } from "@/components/ui/separator";
import { Skeleton } from "@/components/ui/skeleton";
import { getKindeServerSession } from "@kinde-oss/kinde-auth-nextjs/server";
import dynamic from "next/dynamic";
import Image from "next/image";
import Link from "next/link";
import { unstable_noStore as noStore } from "next/cache";

const getData = async (homeId: string) => {
  noStore();
  const data = await prisma.home.findUnique({
    where: {
      id: homeId,
    },
      select: {
      photo: true,
      bathrooms: true,
      guests: true,
      bedrooms: true,
      title: true,
      description: true,
      categoryName: true,
      price: true,
      createdAT: true,
      country: true,
      Reservation: {
        where: {
          homeId: homeId,
        },
      },
      User: {
        select: {
          profileImage: true,
          firstName: true,
        },
      },
    },
  });

  return data;
};
const Page = async ({ params }: { params: { id: string } }) => {
  const data = await getData(params.id);
  const { getAllCountries, getCountryByValue } = useCountries();

  const country = getCountryByValue(data?.country as string);
  const { getUser } = getKindeServerSession();
  const user = await getUser();
  const LazyMap = dynamic(() => import("@/components/Map/Map"), {
    ssr: false,
    loading: () => <Skeleton className=" h-[50vh] w-full " />,
  });

  return (
    <div className=" w-3/4    mx-auto mt-10 mb-12 ">
      <h1 className="font-meduim text-2xl mb-5 ">{data?.title} </h1>
      <div className="relative h-[550px] ">
        <Image
          alt="image of home"
          className="rounded-lg h-full object-cover w-full"
          src={`https://nufhzqmtwqalqdgjepac.supabase.co/storage/v1/object/public/images/${data?.photo}`}
          fill
        />
      </div>
      <div className="flex sm:flex-row flex-col justify-between gap-x-24 mt-8 ">
        <div className="   sm:w-2/3  ">
          <h3 className="text-xl font-medium">
            {" "}
            {country?.flag} {country?.label} / {country?.region}{" "}
          </h3> 
          <div className="flex gap-x-2 text-muted-foreground">
            <p>{data?.guests} guests</p> * <p> {data?.bedrooms} bedrooms</p> *
            <p>{data?.bathrooms} bathrooms</p>
          </div>
          <div className=" flex items-center mt-6">
            <img
              src={
                data?.User?.profileImage ??
                "data:image/png;base64,"
              }
              alt="user profile "
              className="w-11 h-11 rounded-full"
            />
            <div className="flex flex-col ml-4">
              <h3 className="font-medium">
                {" "}
                Hosted by {data?.User?.firstName}
              </h3>
              <h3 className="text-sm text-muted-foreground">
                {" "}
                Hosted since 2020
              </h3>
            </div>
          </div>

          <Separator className="my-7" />
          <CategoryShowCase categoryName={data?.categoryName as string} />
          <Separator className="my-7" />
          <p className="text-muted-foreground"> {data?.description}</p>
          <Separator className="my-7" />
          <LazyMap location={country?.value as string} />
        </div>
        <form action={CreateReservation}>
          <input type="hidden" value={params.id as string} name="homeId" />
          <input type="hidden" value={user?.id as string} name="userId" />
          <SelectCalender reservation={data?.Reservation} />
          {user?.id ? (
            <ReservationSubmitButton />
          ) : (
            <Button className="w-full" asChild>
              <Link href="/api/auth/login">Make a Reservation</Link>
            </Button>
          )}
        </form>
      </div>
    </div>
  );
};

export default Page;
